import '../components/CommonStyle.css';
import * as React from 'react';
// import { Card, Icon } from 'semantic-ui-react'
import LinearProgress from '@mui/material/LinearProgress';
import Box from '@mui/material/Box';
import { EditOutlined, EllipsisOutlined, SettingOutlined } from '@ant-design/icons';
import { Avatar, Card } from 'antd';

const { Meta } = Card;

function MainPage() {
    const [progressArr, setProgressArr] = React.useState(0);
    const [progressLoad, setProgressLoad] = React.useState(0);
    const [progressWay, setProgressWay] = React.useState(0);
    const [progressFinal, setProgressFinal] = React.useState(0);

    React.useEffect(() => {
        const timer = setInterval(() => {
            setProgressArr((oldProgress) => {
                if (oldProgress === 100) {
                    return 100;
                }
                const diff = Math.random() * 10;
                return Math.min(oldProgress + diff, 100);
            });
        }, 500);

        return () => {
            clearInterval(timer);
        };
    }, []);

    React.useEffect(() => {
        if (progressArr === 100) {
            const timer = setInterval(() => {
                setProgressLoad((oldProgress) => {
                    if (oldProgress === 100) {
                        return 100;
                    }
                    const diff = Math.random() * 10;
                    return Math.min(oldProgress + diff, 100);
                });
            }, 500);

            return () => {
                clearInterval(timer);
            };
        }
    }, [progressArr]);

    React.useEffect(() => {
        if (progressLoad === 100) {
            const timer = setInterval(() => {
                setProgressWay((oldProgress) => {
                    if (oldProgress === 100) {
                        return 100;
                    }
                    const diff = Math.random() * 10;
                    return Math.min(oldProgress + diff, 100);
                });
            }, 500);
            return () => {
                clearInterval(timer);
            };
        }
    }, [progressLoad]);

    React.useEffect(() => {
        if (progressWay === 100) {
            const timer = setInterval(() => {
                setProgressFinal((prevProgress) => (prevProgress >= 100 ? 100 : prevProgress + 1));
            }, 800);
            return () => {
                clearInterval(timer);
            };
        }
    }, [progressWay]);

    return(
        <div className="MainPage">
            <Card
                style={{
                    width: 300,
                }}
                // cover={
                //
                // }

                actions={[
                    <Box className={'Row'} sx={{ width: '100%'-100, flex: 1 }}>
                        <LinearProgress
                            value={progressArr}
                            style={{backgroundColor: '#f8f8f8', borderRadius: 100, width: '25%', marginRight: 10}}
                            variant={"determinate"}
                            color="success"
                        />
                        <LinearProgress
                            value={progressLoad}
                            style={{backgroundColor: '#f8f8f8', borderRadius: 100, width: '25%', marginRight: 10}}
                            variant={'determinate'}
                            color="success"
                        />
                        <LinearProgress
                            value={progressWay}
                            style={{backgroundColor: '#f8f8f8', borderRadius: 100, width: '25%', marginRight: 10}}
                            variant={'determinate'}
                            color="success"
                        />
                        <LinearProgress
                            value={progressFinal}
                            style={{backgroundColor: '#f8f8f8', borderRadius: 100, width: '25%'}}
                            variant={'determinate'}
                            color="success"
                        />
                    </Box>
                ]}
            >
                <Meta
                    avatar={<Avatar src="https://joeschmoe.io/api/v1/random" />}
                    title="Card title"
                    description={<Box className={'Row'} sx={{ width: '100%', flex: 1 }}>
                        <LinearProgress
                            value={progressArr}
                            style={{backgroundColor: '#f8f8f8', borderRadius: 100, width: '25%', marginRight: 10}}
                            variant={"determinate"}
                            color="success"
                        />
                        <LinearProgress
                            value={progressLoad}
                            style={{backgroundColor: '#f8f8f8', borderRadius: 100, width: '25%', marginRight: 10}}
                            variant={'determinate'}
                            color="success"
                        />
                        <LinearProgress
                            value={progressWay}
                            style={{backgroundColor: '#f8f8f8', borderRadius: 100, width: '25%', marginRight: 10}}
                            variant={'determinate'}
                            color="success"
                        />
                        <LinearProgress
                            value={progressFinal}
                            style={{backgroundColor: '#f8f8f8', borderRadius: 100, width: '25%'}}
                            variant={'determinate'}
                            color="success"
                        />
                    </Box>}
                />
            </Card>
      </div>
    );
}

export default MainPage;
