import '../components/CommonStyle.css';
import * as React from 'react';
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';
import LinearProgress from '@mui/material/LinearProgress';
import Box from '@mui/material/Box';

// function LinearProgressWithLabel(props) {
//     return (
//         <Box sx={{ display: 'flex', alignItems: 'center' }}>
//             <Box sx={{ width: '100%', mr: 1 }}>
//                 <LinearProgress variant="determinate" {...props} />
//             </Box>
//         </Box>
//     );
// }
//
// LinearProgressWithLabel.propTypes = {
//     /**
//      * The value of the progress indicator for the determinate and buffer variants.
//      * Value between 0 and 100.
//      */
//     value: PropTypes.number.isRequired,
// };

function MainPage() {
    const [progressArr, setProgressArr] = React.useState(0);
    const [progressLoad, setProgressLoad] = React.useState(0);
    const [progressWay, setProgressWay] = React.useState(0);
    const [progressFinal, setProgressFinal] = React.useState(0);

    React.useEffect(() => {
        const timer = setInterval(() => {
            setProgressArr((oldProgress) => {
                if (oldProgress === 100) {
                    return 100;
                }
                const diff = Math.random() * 10;
                return Math.min(oldProgress + diff, 100);
            });
        }, 500);

        return () => {
            clearInterval(timer);
        };
    }, []);

    React.useEffect(() => {
        if (progressArr === 100) {
            const timer = setInterval(() => {
                setProgressLoad((oldProgress) => {
                    if (oldProgress === 100) {
                        return 100;
                    }
                    const diff = Math.random() * 10;
                    return Math.min(oldProgress + diff, 100);
                });
            }, 500);

            return () => {
                clearInterval(timer);
            };
        }
    }, [progressArr]);

    React.useEffect(() => {
        if (progressLoad === 100) {
            const timer = setInterval(() => {
                setProgressWay((oldProgress) => {
                    if (oldProgress === 100) {
                        return 100;
                    }
                    const diff = Math.random() * 10;
                    return Math.min(oldProgress + diff, 100);
                });
            }, 500);
            return () => {
                clearInterval(timer);
            };
        }
    }, [progressLoad]);

    React.useEffect(() => {
        if (progressWay === 100) {
            const timer = setInterval(() => {
                setProgressFinal((prevProgress) => (prevProgress >= 100 ? 100 : prevProgress + 1));
            }, 800);
            return () => {
                clearInterval(timer);
            };
        }
    }, [progressWay]);

    return(
        <div className="MainPage">
            <Card style={{ width: '18rem' }} bg={"primary"} key={'Primary'} >
                <Card.Body>
                    <Card.Title>Card Title</Card.Title>
                    <Card.Text>
                        Some quick example text to build on the card title and make up the
                        bulk of the card's content.
                    </Card.Text>
                </Card.Body>
                <ListGroup className="list-group-flush">
                    <ListGroup.Item>Cras justo odio</ListGroup.Item>
                    <ListGroup.Item>Dapibus ac facilisis in</ListGroup.Item>
                    <ListGroup.Item>Vestibulum at eros</ListGroup.Item>
                </ListGroup>
                <Card.Body>
                    <Card.Link href="#">Card Link</Card.Link>
                    <Card.Link href="#">Another Link</Card.Link>
                    <Box className={'Row'} sx={{ width: '100%', flex: 1 }}>
                        {/*<Box>*/}
                        <LinearProgress
                            value={progressArr}
                            style={{backgroundColor: '#f8f8f8', borderRadius: 100, width: '25%', marginRight: 10}}
                            variant={"determinate"}
                            color="success"
                        />
                        <LinearProgress
                            value={progressLoad}
                            style={{backgroundColor: '#f8f8f8', borderRadius: 100, width: '25%', marginRight: 10}}
                            variant={'determinate'}
                            color="success"
                        />
                        <LinearProgress
                            value={progressWay}
                            style={{backgroundColor: '#f8f8f8', borderRadius: 100, width: '25%', marginRight: 10}}
                            variant={'determinate'}
                            color="success"
                        />
                        <LinearProgress
                            value={progressFinal}
                            style={{backgroundColor: '#f8f8f8', borderRadius: 100, width: '25%'}}
                            variant={'determinate'}
                            color="success"
                        />
                    </Box>
                </Card.Body>
            </Card>
      </div>
    );
}

export default MainPage;
