import '../components/CommonStyle.css';
import * as React from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { Button, CardActionArea, CardActions } from '@mui/material';
import PropTypes from 'prop-types';
import LinearProgress from '@mui/material/LinearProgress';
import Box from '@mui/material/Box';

function LinearProgressWithLabel(props) {
    return (
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <Box sx={{ width: '100%', mr: 1 }}>
                <LinearProgress variant="determinate" {...props} color={'#ffac02'}  />
            </Box>
            <Box sx={{ minWidth: 35 }}>
                <Typography variant="body2" color="green">{`${Math.round(
                    props.value,
                )}%`}</Typography>
            </Box>
        </Box>
    );
}

LinearProgressWithLabel.propTypes = {
    /**
     * The value of the progress indicator for the determinate and buffer variants.
     * Value between 0 and 100.
     */
    value: PropTypes.number.isRequired,
};

function MainPage() {
    const [progress, setProgress] = React.useState(0);

    React.useEffect(() => {
        const timer = setInterval(() => {
            setProgress((prevProgress) => (prevProgress >= 100 ? 10 : prevProgress + 1));
        }, 3600);
        return () => {
            clearInterval(timer);
        };
    }, []);

    return(
      <div className="MainPage">
          <Card sx={{ maxWidth: 345 }}>
              <CardActionArea>
                  <CardContent>
                      <Box sx={{ width: '25%' }}>
                          <LinearProgressWithLabel value={progress} style={{backgroundColor: '#ffac02', color: 'green'}} />
                      </Box>
                      <Typography gutterBottom variant="h5" component="div">
                          Lizard
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                          Lizards are a widespread group of squamate reptiles, with over 6,000
                          species, ranging across all continents except Antarctica
                      </Typography>
                  </CardContent>
              </CardActionArea>
              <CardActions>
                  <Button size="small" color="primary">
                      Share
                  </Button>
              </CardActions>
          </Card>
      </div>
    );
}

export default MainPage;
