function MainPage() {
    const [progress, setProgress] = React.useState(0);

    React.useEffect(() => {
        const timer = setInterval(() => {
            setProgress((oldProgress) => {
                if (oldProgress === 100) {
                    return 100;
                }
                const diff = Math.random() * 10;
                return Math.min(oldProgress + diff, 100);
            });
        }, 500);

        return () => {
            clearInterval(timer);
        };
    }, []);

    return(
      <div className="MainPage">
          <Card sx={{ maxWidth: 345 }}>
              <CardActionArea>
                  <CardContent>
                      <Box sx={{ flexDirection: 'row', justifyContent: 'space-between' }}>
                          <Box>
                            <LinearProgress
                                value={progress}
                                style={{backgroundColor: '#f8f8f8', borderRadius: 100, width: '25%'}}
                                variant={"determinate"}
                                color="success"
                            />
                              {/*<LinearProgress*/}
                              {/*    value={progress}*/}
