import '../components/CommonStyle.css';
import * as React from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { Button, CardActionArea, CardActions } from '@mui/material';
import PropTypes from 'prop-types';
import LinearProgress from '@mui/material/LinearProgress';
import Box from '@mui/material/Box';

// function LinearProgressWithLabel(props) {
//     return (
//         <Box sx={{ display: 'flex', alignItems: 'center' }}>
//             <Box sx={{ width: '100%', mr: 1 }}>
//                 <LinearProgress variant="determinate" {...props} />
//             </Box>
//         </Box>
//     );
// }
//
// LinearProgressWithLabel.propTypes = {
//     /**
//      * The value of the progress indicator for the determinate and buffer variants.
//      * Value between 0 and 100.
//      */
//     value: PropTypes.number.isRequired,
// };

function MainPage() {
    const [progressArr, setProgressArr] = React.useState(0);
    const [progressLoad, setProgressLoad] = React.useState(0);
    const [progressWay, setProgressWay] = React.useState(0);
    const [progressFinal, setProgressFinal] = React.useState(0);

    React.useEffect(() => {
        const timer = setInterval(() => {
            setProgressArr((oldProgress) => {
                if (oldProgress === 100) {
                    return 100;
                }
                const diff = Math.random() * 10;
                return Math.min(oldProgress + diff, 100);
            });
        }, 500);

        return () => {
            clearInterval(timer);
        };
    }, []);

    React.useEffect(() => {
        if (progressArr === 100) {
            const timer = setInterval(() => {
                setProgressLoad((oldProgress) => {
                    if (oldProgress === 100) {
                        return 100;
                    }
                    const diff = Math.random() * 10;
                    return Math.min(oldProgress + diff, 100);
                });
            }, 500);

            return () => {
                clearInterval(timer);
            };
        }
    }, [progressArr]);

    React.useEffect(() => {
        if (progressLoad === 100) {
            const timer = setInterval(() => {
                setProgressWay((oldProgress) => {
                    if (oldProgress === 100) {
                        return 100;
                    }
                    const diff = Math.random() * 10;
                    return Math.min(oldProgress + diff, 100);
                });
            }, 500);
            return () => {
                clearInterval(timer);
            };
        }
    }, [progressLoad]);

    React.useEffect(() => {
        if (progressWay === 100) {
            const timer = setInterval(() => {
                setProgressFinal((prevProgress) => (prevProgress >= 100 ? 0 : prevProgress + 1));
            }, 800);
            return () => {
                clearInterval(timer);
            };
        }
    }, [progressWay]);

    return(
        <div className="MainPage">
          <Card sx={{ maxWidth: 345 }}>
              <CardActionArea>
                  <CardContent>
                      <Box className={'Row'} sx={{ width: '100%', flex: 1 }}>
                          {/*<Box>*/}
                        <LinearProgress
                            value={progressArr}
                            style={{backgroundColor: '#f8f8f8', borderRadius: 100, width: '25%', marginRight: 10}}
                            variant={"determinate"}
                            color="success"
                        />
                        <LinearProgress
                          value={progressLoad}
                          style={{backgroundColor: '#f8f8f8', borderRadius: 100, width: '25%', marginRight: 10}}
                          variant={'determinate'}
                          color="success"
                        />
                        <LinearProgress
                          value={progressWay}
                          style={{backgroundColor: '#f8f8f8', borderRadius: 100, width: '25%', marginRight: 10}}
                          variant={'determinate'}
                          color="success"
                        />
                        <LinearProgress
                          value={progressFinal}
                          style={{backgroundColor: '#f8f8f8', borderRadius: 100, width: '25%'}}
                          variant={'determinate'}
                          color="success"
                        />
                      </Box>
                              {/*<LinearProgress*/}
                              {/*    value={progress}*/}
                              {/*    style={{backgroundColor: '#f8f8f8', borderRadius: 100, width: '25%'}}*/}
                              {/*    color="success" />*/}
                              {/*<LinearProgress*/}
                              {/*    value={progress}*/}
                              {/*    style={{backgroundColor: '#f8f8f8', borderRadius: 100, width: '25%'}}*/}
                              {/*    color="success" />*/}
                          {/*</Box>*/}
                      {/*</Box>*/}
                  </CardContent>
              </CardActionArea>
              <CardActions>
                  <Button size="small" color="primary">
                      Share
                  </Button>
              </CardActions>
          </Card>
      </div>
    );
}

export default MainPage;
